mapper.select:
SELECT SEQ, ID, TITLE, CONTENT, READCOUNT, WDATE
FROM BBS
WHERE SEQ = #{seq}

mapper.update:
UPDATE BBS
SET TITLE = #{title}, CONTENT = #{content}
WHERE SEQ = #{seq}

UPDATE BBS
SET STATUS = 'N'
WHERE SEQ = #{seq}

mapper.select:
SELECT SEQ, ID, TITLE, CONTENT, READCOUNT, WDATE
FROM BBS
WHERE STATUS = 'Y' AND TITLE = #{search}
